console:
// You can attach multiple handlers to a single proxy event
ractive.on( 'activate', function () {
  alert( 'I am also activating!' );
});

javascript:
var ractive = new Ractive({
  el: output,
  template: template
});

ractive.on( 'activate', function ( event ) {
  alert( 'Activating!' );
});

fixed.javascript:
var ractive = new Ractive({
  el: output,
  template: template
});

ractive.on({
  activate: function () {
    alert( 'Activating!' );
  },
  deactivate: function () {
    alert( 'Deactivating!' );
  }
});
